import { useState } from "react"
import { Link } from "react-router-dom"
import {  phoneIcon } from "../../../icons"
import PhoneLogin from "./LoginForm"
import VerifyCode from "./VerifyCode"


const LoginModal = ({type}) => {
    const [ step, setStep ] = useState("phonestep")


    const [phoneNumber, setPhoneNumber] = useState("")
    const [password, setPassword] = useState("")




    return (
        <>
            {step === "phonestep" ? (
                <>
                    {/* container to select phone or google */}
                <div className="login-modal">
                    <div className="title">
                        {type === "login" ? (
                            <h4>Welcome, Sign in</h4>
                        ) : (
                            <h4>Get Started</h4>
                        )}
                    </div>
                { type === "login" ?
                    <div className="login-modes">   
                        <p className="each-mod flex" onClick={() => setStep("login")} ><img src={phoneIcon} alt="phone icon" />Continue with Phone number</p>
                    </div>

                :   <div className="login-modes">   
                        <p className="each-mod flex" onClick={() => setStep("signup")} ><img src={phoneIcon} alt="phone icon" />Continue with Phone number</p>
                    </div>
                }


                    <div className="terms">
                        <p>By continuing, you have read and agreed to Flenjo <Link to="/"> Terms of Service</Link> and <Link to="/">Privacy Policy</Link>.</p>
                    </div>

                    <div className="bottom">
                        {type === "login" ? (
                            <p>Dont have an account? <Link to="/">Create Account</Link></p>
                        ) : (
                            <p>Already have an account? <Link to="/">Sign in</Link></p>
                        )}
                    </div>

                    </div>
                </>

                ) : step === "login" ? (
                    <>
                    {/* login form screeen */}
                    <PhoneLogin 
                        goBack={() => setStep("phonestep")} 
                        setPhoneNumber={setPhoneNumber}
                        setPassword={setPassword}
                        phoneNumber={phoneNumber}
                        password={password}
                    />
                    </>
                ) :  step === "signup" ? (
                    <PhoneLogin 
                    goBack={() => setStep("phonestep")}
                    isSignup={true}
                    verifyPhone={() => setStep("verify")}  
                    setPhoneNumber={setPhoneNumber}
                    setPassword={setPassword}
                    phoneNumber={phoneNumber}
                    password={password}
                    />
                ) :
                step === "verify"? (
                    <VerifyCode phoneNumber={phoneNumber} password={password} goBack={() => setStep("login")} /> 
                ) : 
                null

            }
        </> 
    )

}

export default LoginModal;
