        setModal(false)
    }
    return (
        <div className="home">
            <div className="main-cont">
                <div className="top-bar flex justify-between">
                    <div className="flex justify-around" style={{padding: "8px 8px 8px 8px", alignItems: "center", width: "160px"}}>
                        <div><img src={beamlogo} alt="beamlogo"/></div>
