import { useEffect, useState } from "react"
import { Button } from "../../components/button/Button"
import { Footer } from "../../components/layout/Footer"
import { Modal } from "../../components/modal/Modal"
import LoginModal from "./partials/LoginModal"
import beamlogo from "../../icons/beamlogo.svg"
import caret from "../../icons/caret.svg"
import blackcaret from "../../icons/blackcaret.svg"

const Homepage = () => {
    const [modal, setModal] = useState(false)
    const [type, setType] = useState("login")
    const [activeTab, setActiveTab] = useState()
    const [tabs] = useState([{id: 1}, {id: 2}])

    const changeTab = (tabNumber) => {
        setActiveTab(tabNumber)
    }

    useEffect(() => {
        setActiveTab(1)
    },[])

    const closeModal = () => {
        setModal(false)
    }
    return (
        <div className="home">
            <div className="main-cont">
                <div className="top-bar flex justify-between">
                    <div className="flex justify-around" style={{padding: "8px 8px 8px 8px", alignItems: "center", width: "160px"}}>
                        <div><img src={beamlogo} alt="beamlogo"/></div>
                        <div><p style={{fontFamily: "Graphik",
                            fontStyle: "normal",
                            fontWeight: "normal",
                            color: "#000000", fontSize: "18px",
                            lineHeight: "150%"}}>Orangeboat</p></div>
                        <div><img src={caret} alt="caret"/></div>

                    </div>

                    <div className="flex justify-around" style={{background: "#F6F6F8", alignItems: "center", width: "291px", padding: "8px 8px 8px 16px", borderRadius: "70px"}}>
                        <h3 style={{color: "#5F5F8C"}}>You</h3>
                        <h3 style={{color: "#5F5F8C"}}>Your Team</h3>
                        <h3 style={{color: "#5F5F8C"}}>Admin</h3>
                    </div>

                    <div className="flex justify-around " style={{padding: "8px 8px 8px 8px", alignItems: "center", width: "160px"}}>
                        <div style={{borderRadius: "50%", height: "40px", width: "40px", border: "4px solid #FAFAFA",textAlign: "center",}}>
                        <p style={{color: "#5F5F8C"}}>EU</p>
                        </div>
                        <div><p style={{fontFamily: "Graphik",
                            fontStyle: "normal",
                            fontWeight: "normal",
                            color: "#000000", fontSize: "18px",
                            lineHeight: "150%"}}>Ekene</p></div>
                        <div><img src={blackcaret} alt="caret"/></div>

                    </div>
                </div>

                <div className="body-sect">
                    <div className="top">
                        <h2 style={{fontFamily: "Graphik",
                            fontStyle: "normal",
                            fontWeight: "600",
                            color: "#1E1E2F", fontSize: "25px",
                            lineHeight: "130%"}}>Your Workspace</h2>

                    </div>

                    <div className="regist">
                        <Button text="create account" className="reg-btn" onClick={() => {
                            setModal(true); 
                            setType("signup")
                            }} 
                        />

                        <div className="flex justify-start"> 
                        {tabs.map(tab =>{
                            return <div className={tab === activeTab? 'active': ''} style={{marginRight: "40px"}} onClick={()=>changeTab(tab.id)} >{tab.id}</div>
    //    return <button className={tab === activeTab? 'active': ''} onClick={()=>changeTab(tab.id)}>{tab.id}</button>
    })}
                        </div>

<div className="tab-buttons">
    {tabs.map(tab =>{
       return <button className={tab === activeTab? 'active': ''} onClick={()=>changeTab(tab.id)}>{tab.id}</button>
    })}
    </div>

<div className="tabs">
        <h1>React Tabs</h1>
       <div>
         <div label="Tab 1">
           <div>
