import axios from "axios";
import { getAccessToken } from "../helpers";

const axiosRequest = (requestType, appendUrl, payload) => {

  const baseUrl = `http://flenjodating.herokuapp.com/`; //prone to change based on env
  const requestUrl = `${baseUrl}${appendUrl}`;

  let config;

  if (getAccessToken()) {
    config = {
      headers: {
        "Content-type": "Application/json",
        Authorization: `Bearer ${getAccessToken()}`,
      },
    };
  } else {
    config = {
      headers: {
        "Content-type": "Application/json",
      },
    };
  }

  const axiosGet = () => {
    return new Promise((resolve, reject) => {
      axios
        .get(requestUrl)
        .then((res) => {
          resolve(res);
        })
        .catch((error) => {
          reject(error);
        });
    });
  };

  const axiosPost = () => {
    return new Promise((resolve, reject) => {
      axios
        .post(requestUrl, payload, config)
        .then((res) => {
          resolve(res);
        })
        .catch((error) => {
          console.log(error);
          reject(error);
        });
    });
  };

  const axiosDelete = () => {
    // return new Promise((resolve, reject) => {
    //   axios.delete(requestUrl, config).then(res => {
    //       resolve(res);
    //   })
    //   .catch(error => {
    //       reject(error)
    //   })
    // });
  };

  const axiosPut = () => {
    return new Promise((resolve, reject) => {
      axios
        .put(requestUrl, payload, config)
        .then((res) => {
          resolve(res);
        })
        .catch((error) => {
          reject(error);
        });
    });
  };

  switch (requestType.toLowerCase()) {
    case "post":
      return axiosPost();

    case "get":
      return axiosGet();

    case "delete":
      return axiosDelete();

    case "put":
      return axiosPut();

    default:
      break;
  }
};

export default axiosRequest;
