import { useEffect, useState } from "react"
import { Button, Input } from "../../components";
import babe from "../../images/babe.png";
import { apiRoutes } from "../../utils/apiRoutes";
import axiosRequest from "../../utils/apiservices";
import { showToast } from "../../utils";

export const UploadPhotos = () => {

    const [one, setOne] = useState({
        id: 0,
        url: ""
    });
    const [two, setTwo] = useState({
        id: 0,
        url: ""
    });
    const [three, setThree] = useState({
        id: 0,
        url: ""
    });
    const [four, setFour] = useState({
        id: 0,
        url: ""
    });
    const [five, setFive] = useState({
        id: 0,
        url: ""
    });
    const [six, setSix] = useState({
        id: 0,
        url: ""
    });

    const [allPhotos] = useState([])

    const handleInputChangeone = async (e) => {
        await uploadFile(1, e.target.files[0])
    }
    const handleInputChangetwo = async (e) => {
        await uploadFile(2, e.target.files[0])
    }
    const handleInputChangethree = async (e) => {
        await uploadFile(3, e.target.files[0])
    }

    const handleInputChangefour = async (e) => {
        await uploadFile(4, e.target.files[0])
    }

    const handleInputChangefive = async (e) => {
        await uploadFile(5, e.target.files[0])
    }

    const handleInputChangesix = async (e) => {
        await uploadFile(6, e.target.files[0])
    }

    const uploadFile = async (id, data) => {

        // e.preventDefault();
        const formData = new FormData();
        formData.append("file", data);
        await axiosRequest("post", apiRoutes.uploadFile, formData)
            .then((res) => {
                console.log(res?.data)
                // setAllPhotos({id, url: res.data.data.url})
                allPhotos.push({id, url: res.data.data.url});

                if(id === 1){
                    setOne({id, url: res.data.data.url})
                }else if(id ===2){
                    setTwo({id, url: res.data.data.url})
                }else if(id ===3){
                    setFour({id, url: res.data.data.url})
                }else if(id ===4){
                    setFive({id, url: res.data.data.url})
                }else if(id ===5){
                    setFive({id, url: res.data.data.url})
                }else {
                    setSix({id, url: res.data.data.url})
                }
                if(res?.data){
                    showToast(res?.data?.messsage || "Your request was successful", "success")
                }
            })
            .catch(err => {
                if(err?.response === undefined){
                    return showToast("No internet connection", "error")
                }
                // let {data} = err.response;
                if(err?.response?.data){
                    showToast(err?.response?.data?.error, "error")
                } else{
                    showToast("!Oops error uploading photo. Try again", "error")
                }
            })
    }

    const uploadAllPhotos = async (e) => {

        await axiosRequest("put", apiRoutes.uploadPhotos, {pictures: allPhotos})
            .then((res) => {
                console.log(res)

                if(res?.data){
                    showToast(res?.data?.messsage || "Your request was successful", "success")
                }
            })
            .catch(err => {
                if(err?.response === undefined){
                    return showToast("No internet connection", "error")
                }
                let {data} = err.response;
                if(data){
                    showToast(data.error, "error")
                } else{
                    showToast("!Oops something went wrong. We are looking at it", "error")
                }
            })
    }


    return (
        <>

            <div className="account-cont flex justify-between">
                <div className="left w-1/2">
                    <div className="-page-title" style={{marginBottom: "50px"}}>
                        <h3>Add Profile Pictures</h3>
                        <p style={{color: "#7B8192"}}>You need to add a minimum of 2 images before you can continue</p>
                    </div>
                    <div class="grid grid-cols-3 gap-4">
                    <div style={{backgroundColor: "#F6F6F6", backgroundImage:`url(${one.url})`, height: "194px", width: "width: 154px", borderRadius: "12px"}}>
                            <div style={{padding: "30px",   display: "flex", justifyContent: "center", marginTop: "70px", borderRadius: "50%", backgroundColor: "blue"}}>
                                <input type="file" onChange={handleInputChangeone}/>
                            </div>
                        </div>
                        <div style={{backgroundColor: "#F6F6F6",backgroundImage:`url(${two.url})`, height: "194px", width: "width: 154px", borderRadius: "12px"}}>
                            <div style={{padding: "30px",   display: "flex", justifyContent: "center", marginTop: "70px", borderRadius: "50%", backgroundColor: "blue"}}>
                                <input type="file" onChange={handleInputChangetwo}/>
                            </div>
                        </div>
                        <div style={{backgroundColor: "#F6F6F6",backgroundImage:`url(${three.url})`, height: "194px", width: "width: 154px", borderRadius: "12px"}}>
                            <div style={{padding: "30px",   display: "flex", justifyContent: "center", marginTop: "70px", borderRadius: "50%", backgroundColor: "blue"}}>
                                <input type="file" onChange={handleInputChangethree}/>
                            </div>
                        </div>
                        <div style={{backgroundColor: "#F6F6F6",backgroundImage:`url(${four.url})`, height: "194px", width: "width: 154px", borderRadius: "12px"}}>
                            <div style={{padding: "30px",   display: "flex", justifyContent: "center", marginTop: "70px", borderRadius: "50%", backgroundColor: "blue"}}>
                                <input type="file" onChange={handleInputChangefour}/>
                            </div>
                        </div>
                        <div style={{backgroundColor: "#F6F6F6",backgroundImage:`url(${five.url})`, height: "194px", width: "width: 154px", borderRadius: "12px"}}>
                            <div style={{padding: "30px",   display: "flex", justifyContent: "center", marginTop: "70px", borderRadius: "50%", backgroundColor: "blue"}}>
                                <input type="file" onChange={handleInputChangefive}/>
                            </div>
                        </div>
                        <div style={{backgroundColor: "#F6F6F6",backgroundImage:`url(${six.url})`, height: "194px", width: "width: 154px", borderRadius: "12px"}}>
                            <div style={{padding: "30px",   display: "flex", justifyContent: "center", marginTop: "70px", borderRadius: "50%", backgroundColor: "blue"}}>
                                <input type="file" onChange={handleInputChangesix}/>
                            </div>
                        </div>
                    </div>
                    <form onSubmit={uploadAllPhotos}>
                        <div class="grid mt-20">
                            <Button
                            type="submit"
                            text="Complete Setup"
                            className="uploadphoto-btn"
                            disabled={allPhotos.length < 2}
                            />
                        </div>
                    </form>

                </div>
                <div className="right w-1/2">
                    <img src={babe} alt="Girl" />
                </div>
            </div>

        </>
    )
}
