import { Button } from "../../../components";
import OtpInput from "react-otp-input";
import { useState } from "react";
import { useHistory } from "react-router-dom";
import { useDispatch } from "react-redux";
import { SIGNUP } from "../../../redux/actions/types";

import axiosRequest from "../../../utils/apiservices";
import { apiRoutes } from "../../../utils/apiRoutes";
import { encryptData } from "../../../helpers";

export const VerifyCode = ({ goBack, phoneNumber, password }) => {

  const [otp, setOtp] = useState("");
  const [loading, setLoading] = useState(false);
  const [termsAndCondition] = useState(true);

  const history = useHistory();

  const dispatch = useDispatch();

const phoneVerification = async () => {
    setLoading(!loading);

    try {
        const { data } = await axiosRequest("post", apiRoutes.signup, {
          code: otp,
          phone: phoneNumber.toString(),
          password
        });
        const { token, user } = data.data;

        localStorage.setItem("user", encryptData(user))
        localStorage.setItem("access_token", encryptData(token.access))

        dispatch({type: SIGNUP.SUCCESS, payload: user});
        history.push("/accountsettings" + termsAndCondition);

      } catch (err) {
          console.log(err)
        //err handler here and notification if needed
      } finally {
        setLoading(false);
    }
};



  const resendVerificationCode = async () => {
    try {
      await axiosRequest("post", apiRoutes.verificationCode, {
        type: "phone",
        phone: "0" + phoneNumber.toString(),
      });
    } catch (err) {
      //err handler here and notification if needed
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    phoneVerification();
  };

  return (
    <form className="phone" onSubmit={handleSubmit}>
      <div className="form-title">
        <h4>Enter Verification Code</h4>
        <div className="code-resend flex justify-between">
          <p>
            Provide the code sent to <span>{`0${phoneNumber}`}</span>
          </p>
          <p style={{ cursor: "pointer" }} onClick={resendVerificationCode}>
            Resend
          </p>
        </div>
      </div>

      <div className=" mt-3">
        <OtpInput
          value={otp}
          onChange={(otp) => setOtp(otp)}
          shouldAutoFocus
          numInputs={6}
          separator={<span>&nbsp; &nbsp; </span>}
          inputStyle="otp-input"
          containerStyle="otp-cont"
        />
      </div>

      <Button
        text="continue"
        className="phone-cont"
        disabled={otp.length < 6 || loading}
      />

      <p className="back-btn" onClick={goBack}>
        Back
      </p>
    </form>
  );
};

export default VerifyCode;
