import React, { useState } from "react";
import { Button, Input } from "../../../components";
import axiosRequest from "../../../utils/apiservices";
import { apiRoutes } from "../../../utils/apiRoutes";
import { encryptData } from "../../../helpers";
import { useDispatch } from "react-redux";
import { SIGNUP } from "../../../redux/actions/types";
import { useHistory } from "react-router-dom";
import { showToast } from "../../../utils";


const PhoneLogin = ({ verifyPhone, goBack, setPhoneNumber, phoneNumber, setPassword, password, isSignup }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);

  const dispatch = useDispatch();
  const history = useHistory();


  const handleSubmit = async (e) => {
    e.preventDefault();

    setIsSubmitting(true);

    try {
      if(isSignup){
        await axiosRequest("post", apiRoutes.verificationCode, {
          type: "phone",
          phone: "0" + phoneNumber.toString(),
        });

        verifyPhone();
      } else {
        const { data } = await axiosRequest("post", apiRoutes.login, {
          password,
          phone: phoneNumber.toString(),
        });

        const { token, user } = data.data;
        localStorage.setItem("user", encryptData(user))
        localStorage.setItem("access_token", encryptData(token.access))
        dispatch({type: SIGNUP.SUCCESS, payload: user});

        // redirect to dashboard
        if(!user.firstName || !user.lastName || !user.gender){
          return history.push("/accountsettings", {termsAndCondition: true});
        }

        localStorage.setItem("user", encryptData(user))
        localStorage.setItem("access_token", encryptData(token.access))
        showToast(data?.message || "Your request was successful", "success");

        dispatch({type: SIGNUP.SUCCESS, payload: user});
        history.push("/dashboard");
      }
    } catch (err) {
      //err handler here and notification if needed
      let {data} = err?.response;
      if(data){
          return showToast(data?.error, "error")
      } else{
          showToast("!Oops something went wrong. We are looking at it", "error")
      }

    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e) => {
    let val = e.target.value;
    if (val.length === 11) return;
    setPhoneNumber(val);
  };

  const handlePasswordChange = (e) => {
    let val = e.target.value;
    setPassword(val);
  };

  return (
    <form className="phone" onSubmit={handleSubmit}>
      <div className="form-title">
        <h4>Enter Phone number</h4>
        <p>
          We will send a text with verification code. Message and data rate may
          apply.{" "}
        </p>
      </div>

      <div className="phone-div mt-3 flex justify-between">
        <button disabled>+234</button>

        <Input
          placeholder="80xxxxxxxx"
          name="phoneNumber"
          type="number"
          className="number-input"
          value={phoneNumber}
          onChange={handleChange}
          required
        />

      </div>
      <div className="phone-div mt-3 flex justify-between">

      <Input
          placeholder="Enter your password"
          name="password"
          type="password"
          // className="number-input"
          value={password}
          onChange={handlePasswordChange}
          required
        />
      </div>
      <Button
        text="continue"
        className="phone-cont"
        type="submit"
        disabled={phoneNumber.length < 10 || isSubmitting || password.length < 6}
      />

      <p className="back-btn" onClick={() => goBack()}>
        Back
      </p>
    </form>
  );
};

export default PhoneLogin;
