import { useState } from "react"
import { Button, Input } from "../../components";
import { Checkbox } from "../../components/input/Checkbox";
import { Modal } from "../../components/modal/Modal";
import { femaleIcon, maleIcon } from "../../icons";
import babe from "../../images/babe.png";
import TermsModal from "./partials/Terms";
import { useFormik } from "formik";
import { profileSchema } from "../../validators/accountsettings";
import { apiRoutes } from "../../utils/apiRoutes";
import axiosRequest from "../../utils/apiservices";
import { useDispatch } from "react-redux";
import { showToast } from "../../utils";

export const UploadPhotos = () => {
    const [modal, setModal] = useState(true)

    const dispatch = useDispatch();

    const closeModal = () => {
        setModal(false)
    }

    const passion = [
        {id: 1, item: "swimming"},
        {id: 2, item: "foodie"},
        {id: 3, item: "driving"},
        {id: 4, item: "drumming"},
        {id: 5, item: "reading"},
        {id: 6, item: "singing"},
    ]

    const [lastName, setLastName] = useState("");
    const [firstName, setFirstName] = useState("");
    const [gender, setGender] = useState("");
    const [passions, setPassions] = useState([]);
    const [email, setEmail] = useState("");
    const [day, setDay] = useState("");
    const [month, setMonth] = useState("");
    const [year, setYear] = useState("");


    const handleInputChangefirstname = (e) => {
        let value = e.target.value;

			setFirstName(value)
    }
            const handleInputChangelastname = (e) => {
                let value = e.target.value;

			setLastName(value)
            }
            const handleInputChangemonth = (e) => {
                    let value = e.target.value;
            }

    const handleSubmit = async (e) => {

        await axiosRequest("put", apiRoutes.updateProfile, {})
            .then((res) => {
                console.log(res)

                if(res?.data){
                    showToast(res?.data?.messsage || "Your request was successful", "success")
                }

            })
            .catch(err => {
                let {data} = err.response;
                if(data){
                    return showToast(data.error, "error")
                } else{
                    showToast("!Oops something went wrong. We are looking at it", "error")
                }
            })
    }


    // const formik = useFormik({
    //     "",
    //     onSubmit: handleSubmit,
    //     // validationSchema: profileSchema,
    //     validationOnChange: false,
    // })


    return (
        <>

            <div className="account-cont flex justify-between">
                <div className="left w-1/2">
                    <div className="-page-title">
                        <h3>Set up your Flenjo account</h3>
                    </div>

                    <div className="details-fill ">
                        <form onSubmit={handleSubmit}>
                            <div className="name-cont flex">
                                <div className="w-1/2 ">
                                    <Input
                                        label="Surname (Family name)"
                                        value={lastName}
                                        name="lastName"
                                        onChange={ handleInputChangelastname}
                                        placeholder="Surname"
                                        className="ml-0"
                                        // errorMessage={formik?.errors?.lastName}
                                    />
                                </div>
                                <div className="w-1/2 ">
                                    <Input
                                        label="First Name"
                                        value={firstName}
                                        name="firstName"
                                        onChange={ handleInputChangefirstname}
                                        placeholder="First name"
                                        // errorMessage={formik?.errors?.firstName}
                                    />
                                </div>
                            </div>

                            <div className="sec-cont flex">
                                <div className="w-1/2 ">
                                    <Input
                                        label="Email"
                                        name="email"
                                        value={email}
                                        onChange={ handleInputChangeemail}
                                        placeholder="Precaptured email"
                                        // errorMessage={formik?.errors?.email}
                                    />
                                </div>
                                <div className="w-1/2 flex justify-between">
                                    <Input
                                        label="Birthday"
                                        name="month"
                                        value={month}
                                        onChange={ handleInputChangemonth}
                                        placeholder="MM"
                                        className="text-center"
                                        // errorMessage={formik?.errors?.month}
                                    />
                                    <Input
                                        label=""
                                        name="day"
                                        value={day}
                                        onChange={ handleInputChangeday}
                                        placeholder="DD"
                                        className="mt-8 text-center"
                                        // errorMessage={formik?.errors?.day}
                                    />
                                    <Input
                                        name="year"
                                        value={year}
                                        onChange={ handleInputChangeyear}
                                        placeholder="YY"                                   
                                        className="mt-8 text-center"
                                        // errorMessage={formik?.errors?.year}
                                    />
                                </div>
                            </div>

                            <div className="gender-sect">
                                <p className="gender-text">Gender</p>
                                <div className="gender-select flex">
                                    <div className="gender male flex">
                                        <label>
                                            <>
                                                <img src={maleIcon} alt="MALE" /> 
                                                <span>Male</span>
                                            </>        
                                            <input type="radio" name="gender" value={gender} onChange={ handleInputChangegender} />
                                        </label>

                                    </div>
                                    <div className="gender female flex">
                                        <label>'
                                            <>
                                                <img src={femaleIcon} alt="MALE" /> 
                                                <span>Male</span>
                                            </>
                                            <input type="radio" name="gender" value={gender} onChange={ handleInputChangegender} />
                                        </label>
                                    </div>
                                    {/* <div className="gender male flex"><img src={maleIcon} alt="MALE" /> <span>Male</span></div>
                                    <div className="gender female flex"><img src={femaleIcon} alt="FEMALE" /><span>Female</span></div> */}
                                </div>
                                <div className="show-gender"><Checkbox label="show my gender on my profile" /></div>
                            </div>

                            <div className="passion ">
                                <p className="optional">Choose your passion (Optional)</p>
                                <br />
                                <span>Let everyone know what you are passionate about</span>

                                <div className="select-passion flex">
                                    {passion?.map((item) => (
                                        <div className="item" key={item?.id}>
                                            {item?.item}
                                        </div>
                                    ))}
                                </div>

                                <div className="more"><span>+</span> Show more</div>
                            </div>

                            <div className="continue-btn">
                                <Button text="Continue" className="settings-btn"/>
                            </div>
                        </form>
                    </div>
                </div>
                <div className="right w-1/2">
                    <img src={babe} alt="Girl" />
                </div>
            </div>

            <Modal onClose={closeModal} visible={modal}>
                <TermsModal close={closeModal} />
            </Modal>
        </>
    )
}
