import { useEffect, useState } from "react"
import { Button, Input } from "../../components";
import { Checkbox } from "../../components/input/Checkbox";
import { Modal } from "../../components/modal/Modal";
import { femaleIcon, maleIcon } from "../../icons";
import babe from "../../images/babe.png";
import TermsModal from "./partials/Terms";
import { apiRoutes } from "../../utils/apiRoutes";
import axiosRequest from "../../utils/apiservices";
import { session, showToast } from "../../utils";
import { useHistory, useLocation } from "react-router-dom";

// import {createBrowserHistory} from 'history'

export const AccountSetup = () => {
    const [modal, setModal] = useState(false)
    const [termsAndCondition, setTermsAndCondition] = useState(false)
    const [passionModal, setPassion] = useState(false)

    // const stateHistory = createBrowserHistory();
    const history = useHistory();
    const {state} = useLocation();

    useEffect(()=> {
        console.log("state", state)
        if (!session.get("modalShowed") && state?.termsAndCondition) {
            setModal(true)
            setTermsAndCondition(true)
            // let state = { ...stateHistory.location.state };
            // setTermsAndCondition(state.termsAndCondition)

            // delete state.termsAndCondition;

            // stateHistory.replace({ ...stateHistory.location, state });
        } else {
            setTermsAndCondition(false)
            session.remove("modalShowed")
        }
    }, [state])

    const closeModal = () => {
        setModal(false)
        setTermsAndCondition(false)
        // setPassion(false)
    }

    let passion = [
        {id: 1, item: "swimming"},
        {id: 2, item: "foodie"},
        {id: 3, item: "driving"},
        {id: 4, item: "drumming"},
        {id: 5, item: "reading"},
        {id: 6, item: "singing"},
        {id: 7, item: "dancing"},
        {id: 8, item: "hiking"},
        {id: 9, item: "adventurous"},
        {id: 10, item: "sleeping"},
        {id: 11, item: "running"},
        {id: 12, item: "soccer"},
    ]

    const [lastName, setLastName] = useState("");
    const [firstName, setFirstName] = useState("");
    const [gender, setGender] = useState("");
    const [email, setEmail] = useState("");
    const [day, setDay] = useState("");
    const [month, setMonth] = useState("");
    const [year, setYear] = useState("");

    const [selectedPassion, setPassionArr] = useState([])

    const handleInputChangefirstname = (e) => {
        let value = e.target.value;

		setFirstName(value)
    }
    const handleInputChangelastname = (e) => {
        let value = e.target.value;

    setLastName(value)
    }
    const handleInputChangemonth = (e) => {
            let value = e.target.value;

    setMonth(value)
        }
    const handleInputChangeday = (e) => {
        let value = e.target.value;

    setDay(value)
    }
    const handleInputChangeyear = (e) => {
        let value = e.target.value;

    setYear(value)
    }

    const handleInputChangeemail = (e) => {
        let value = e.target.value;

    setEmail(value)
    }
    const handleInputChangegender = (e) => {
        let value = e.target.value;

        setGender(value)
        console.log(value)
    }


    const handleSubmit = async (e) => {
        e.preventDefault()
        const data = {
            firstName: firstName,
            lastName: lastName,
            gender: gender,
            passions: selectedPassion,
            email: email
        }
        const {day, month, year} = data
        data.dob = `${day}-${month}-${year}`;
        delete data.day
        delete data.month
        delete data.year

        await axiosRequest("put", apiRoutes.updateProfile, data)
            .then((res) => {
                console.log(res)

                if(res?.data){
                    showToast(res?.data?.data?.messsage || "Your request was successful", "success")
                }
                history.push("/uploadphotos");

            })
            .catch(err => {
                let {data} = err.response;
                if(data){
                    return showToast(data.error, "error")
                } else{
                    showToast("!Oops something went wrong. We are looking at it", "error")
                }
            })
    }

    const handlePassion =  (item) => {
        const index = passion.indexOf(item);
        passion.splice(index, 1);
        setPassionArr([...selectedPassion, item]);
    }

    const PassionsModal = ( ) => {
        return (
            <>
                <div className="mx-auto flex flex-wrap justify-content select-passion-modal ">
                    {passion?.map((item, i) => (
                        <div className="item mb-2" key={i} onClick={() => handlePassion(item)}>
                            {item?.item}
                        </div>
                    ))}
                </div>
            </>
        )
    }

    return (
        <>
            <div className="account-cont flex justify-between">
                <div className="left w-1/2">
                    <div className="-page-title">
                        <h3>Set up your Flenjo account</h3>
                    </div>

                    <div className="details-fill ">
                        <form onSubmit={handleSubmit}>
                            <div className="name-cont flex">
                                <div className="w-1/2 ">
                                    <Input
                                        label="Surname"
                                        // label="Surname (Family name)"
                                        value={lastName}
                                        name="lastName"
                                        onChange={ handleInputChangelastname}
                                        placeholder="Surname"
                                        className="ml-0"
                                        // errorMessage={formik?.errors?.lastName}
                                    />
                                </div>
                                <div className="w-1/2 ">
                                    <Input
                                        label="First Name"
                                        value={firstName}
                                        name="firstName"
                                        onChange={ handleInputChangefirstname}
                                        placeholder="First name"
                                        // errorMessage={formik?.errors?.firstName}
                                    />
                                </div>
                            </div>

                            <div className="sec-cont flex">
                                <div className="w-1/2 ">
                                    <Input
                                        label="Email"
                                        name="email"
                                        value={email}
                                        onChange={ handleInputChangeemail}
                                        placeholder="Precaptured email"
                                        // errorMessage={formik?.errors?.email}
                                    />
                                </div>
                                <div className="w-1/2 flex justify-between">
                                    <Input
                                        label="Birthday"
                                        name="month"
                                        value={month}
                                        onChange={ handleInputChangemonth}
                                        placeholder="MM"
                                        className="text-center date"
                                        // errorMessage={formik?.errors?.month}
                                    />
                                    <Input
                                        label=""
                                        name="day"
                                        value={day}
                                        onChange={ handleInputChangeday}
                                        placeholder="DD"
                                        className="mt-8 text-center date"
                                        // errorMessage={formik?.errors?.day}
                                    />
                                    <Input
                                        name="year"
                                        value={year}
                                        onChange={ handleInputChangeyear}
                                        placeholder="YYYY"                                   
                                        className="mt-8 text-center date"
                                        // errorMessage={formik?.errors?.year}
                                    />
                                </div>
                            </div>

                            <div className="gender-sect">
                                <p className="gender-text">Gender</p>
                                <div className="gender-select flex">
                                    <div className={`gender male flex ${gender === "male" ? "selected" : ""}`}>
                                        <label>
                                            <div className="flex">
                                                <img src={maleIcon} alt="MALE" className="mr-2" /> 
                                                <span>Male</span>
                                            </div>        
                                            <input type="radio" name="gender" value="male" onChange={ handleInputChangegender} className="hidden" />
                                        </label>

                                    </div>
                                    <div className={`gender female flex ${gender === "female" ? "selected" : ""}`}>
                                        <label>
                                            <div className="flex">
                                                <img src={femaleIcon} alt="female" className="mr-2" /> 
                                                <span>Female</span>
                                            </div>
                                            <input type="radio" name="gender" value="female" onChange={ handleInputChangegender} className="hidden"/>
                                        </label>
                                    </div>
                                    {/* <div className="gender male flex"><img src={maleIcon} alt="MALE" /> <span>Male</span></div>
                                    <div className="gender female flex"><img src={femaleIcon} alt="FEMALE" /><span>Female</span></div> */}
                                </div>
                                <div className="show-gender"><Checkbox label="show my gender on my profile" /></div>
                            </div>

                            <div className="passion ">
                                <p className="optional">Choose your passion (Optional)</p>
                                <br />
                                <span>Let everyone know what you are passionate about</span>

                                <div className="select-passion flex flex-wrap ">
                                    {selectedPassion?.map((item, i) => (
                                        <div className="item mb-2" key={i}>
                                            {item}
                                        </div>
                                    ))}
                                </div>

                                {passionModal ? 
                                <Modal onClose={closeModal} visible={passionModal} >
                                    <PassionsModal close={closeModal} />
                                </Modal>   
                                : "" }

                                <div className="more" onClick={() => setPassion(true)} ><span>+</span> Show more</div>
                            </div>

                            <div className="continue-btn">
                                <Button text="Continue" className="settings-btn"/>
                            </div>
                        </form>
                    </div>
                </div>
                <div className="right w-1/2">
                    <img src={babe} alt="Girl" />
                </div>
            </div>
            {termsAndCondition ?
                <Modal visible={modal} removeCloseIcon={true}>
                    <TermsModal close={() => {
                        session.set("modalShowed", "true")
                        closeModal()
                    }}/>
                </Modal>
            : ""
            }
        </>
    )
}
