import { useState } from "react"
import { Button, Input } from "../../components";
import babe from "../../images/babe.png";
import { apiRoutes } from "../../utils/apiRoutes";
import axiosRequest from "../../utils/apiservices";
import { showToast } from "../../utils";

export const UploadPhotos = () => {

    const [one, setOne] = useState({
        id: 0,
        url: ""
    });
    const [two, setTwo] = useState();
    const [three, setThree] = useState();
    const [four, setFour] = useState();
    const [five, setFive] = useState();
    const [six, setSix] = useState();


    const handleInputChangeone = async (e) => {
        await uploadFile(1, e.target.files[0])
    }
    const handleInputChangetwo = (e) => {
        let value = e.target.files[0];

        setTwo(value)
    }
    const handleInputChangethree = (e) => {
            let value = e.target.files[0];
        setThree(value)

    }

    const handleInputChangefour = (e) => {
        let value = e.target.files[0];
        setFour(value)
    }

    const handleInputChangefive = (e) => {
        let value = e.target.files[0];
        setFive(value)
    }

    const handleInputChangesix = (e) => {
        let value = e.target.files[0];
        setSix(value)
    }

    const uploadFile = async (id, data) => {

        // e.preventDefault();
        const formData = new FormData();
        formData.append("file", data);
        await axiosRequest("post", apiRoutes.uploadFile, formData)
            .then((res) => {
                console.log(res?.data)
                if(id === 1){
                    setOne({id, url: res.data.data.url})
                }else if(id ===2){
                    setTwo({id, url: res.data.data.url})
                }else if(id ===3){
                    setFour({id, url: res.data.data.url})
                }else if(id ===4){
                    setFive({id, url: res.data.data.url})
                }else if(id ===5){
                    setFive({id, url: res.data.data.url})
                }else {
                    setSix({id, url: res.data.data.url})
                }
                if(res?.data){
                    showToast(res?.data?.messsage || "Your request was successful", "success")
                }
            })
            .catch(err => {
                if(err?.response === undefined){
                    return showToast("No internet connection", "error")
                }
                // let {data} = err.response;
                if(err?.response?.data){
                    return showToast(err?.response?.data?.error, "error")
                } else{
                    showToast("!Oops error uploading photo. Try again", "error")
                }
            })
    }

    const uploadAllPhotos = async (e) => {

        await axiosRequest("put", apiRoutes.uploadPhotos, {})
            .then((res) => {
                console.log(res)

                if(res?.data){
                    showToast(res?.data?.messsage || "Your request was successful", "success")
                }
            })
            .catch(err => {
                if(err?.response === undefined){
                    return showToast("No internet connection", "error")
                }
                let {data} = err.response;
                if(data){
                    showToast(data.error, "error")
                } else{
                    showToast("!Oops something went wrong. We are looking at it", "error")
                }
            })
    }


    return (
        <>

            <div className="account-cont flex justify-between">
                <div className="left w-1/2">
                    <div className="-page-title" style={{marginBottom: "50px"}}>
                        <h3>Add Profile Pictures</h3>
                        <p style={{color: "#7B8192"}}>You need to add a minimum of 2 images before you can continue</p>
                    </div>
                    <div class="grid grid-cols-3 gap-4">
                    <div style={{backgroundColor: "#F6F6F6", height: "194px", width: "width: 154px", borderRadius: "12px"}}>

                        </div>
                        <div style={{backgroundColor: "#F6F6F6", height: "194px", width: "width: 154px", borderRadius: "12px"}}>
                                <div style={{padding: "30px",   display: "flex", justifyContent: "center", marginTop: "70px", borderRadius: "50%", backgroundColor: "blue"}}>

                                </div>
                                {/* <form className="phone" onSubmit={() => uploadFile(1)}> */}
                                <input type="file" onChange={(e) => handleInputChangeone(e, 1)}/>
                                <img alt="" src={one.url} style={{width: "100%", height: "100%"}}/>

                                {/* </form> */}
                        </div>
                        <div style={{backgroundColor: "#F6F6F6", height: "194px", width: "width: 154px", borderRadius: "12px"}}>

                        </div>
                        <div style={{backgroundColor: "#F6F6F6", height: "194px", width: "width: 154px", borderRadius: "12px"}}>

                        </div>
                        <div style={{backgroundColor: "#F6F6F6", height: "194px", width: "width: 154px", borderRadius: "12px"}}>

                        </div>
                        <div style={{backgroundColor: "#F6F6F6", height: "194px", width: "width: 154px", borderRadius: "12px"}}>

                        </div>
                    </div>

                    <div class="grid mt-20">
                            <Button text="Complete Setup" className="uploadphoto-btn"/>
                    </div>

                </div>
                <div className="right w-1/2">
                    <img src={babe} alt="Girl" />
                </div>
            </div>

        </>
    )
}
