import { useEffect, useState } from "react"
import { Button, Input } from "../../components";
import { Checkbox } from "../../components/input/Checkbox";
import { Modal } from "../../components/modal/Modal";
import { femaleIcon, maleIcon } from "../../icons";
import babe from "../../images/babe.png";
import TermsModal from "./partials/Terms";
import { useFormik } from "formik";
import { profileSchema } from "../../validators/accountsettings";
import { apiRoutes } from "../../utils/apiRoutes";
import axiosRequest from "../../utils/apiservices";
import { useDispatch } from "react-redux";
import { showToast } from "../../utils";
import { useHistory, useLocation } from "react-router-dom";

export const AccountSetup = () => {
    const [modal, setModal] = useState(false)
    const [termsAndCondition, setTermsAndCondition] = useState(true)

    const dispatch = useDispatch();
    const history = useHistory();
    const location = useLocation();
    // const getTermsAndCondition = state.termsAndCondition;

    const closeModal = () => {
        setModal(false)
        setTermsAndCondition(false)
    }
    // useEffect(()=> {
    //     let state = { ...location.state };


    //     // localStorage.setItem("termsAndCondition", getTermsAndCondition)
    //     // const newTermsAndCondition = JSON.parse(localStorage.getItem("termsAndCondition"));
    //     setTermsAndCondition(state.termsAndCondition)
    //     delete state.termsAndCondition;
    //     console.log("sateeeeee", termsAndCondition)
    // }, [termsAndCondition, location.state])

    const passion = [
        {id: 1, item: "swimming"},
        {id: 2, item: "foodie"},
        {id: 3, item: "driving"},
        {id: 4, item: "drumming"},
        {id: 5, item: "reading"},
        {id: 6, item: "singing"},
    ]

    const [lastName, setLastName] = useState("");
    const [firstName, setFirstName] = useState("");
    const [gender, setGender] = useState("");
    const [passions, setPassions] = useState([]);
    const [email, setEmail] = useState("");
    const [day, setDay] = useState("");
    const [month, setMonth] = useState("");
    const [year, setYear] = useState("");


    const handleInputChangefirstname = (e) => {
        let value = e.target.value;

			setFirstName(value)
    }
            const handleInputChangelastname = (e) => {
                let value = e.target.value;

			setLastName(value)
            }
            const handleInputChangemonth = (e) => {
                    let value = e.target.value;

			setMonth(value)
                }
            const handleInputChangeday = (e) => {
                let value = e.target.value;

            setDay(value)
            }
            const handleInputChangeyear = (e) => {
                let value = e.target.value;

            setYear(value)
            }
            const handleInputChangepassions =  (e) =>{
                let value = e.target.value;

			setPassions(value)
            }
            const handleInputChangeemail = (e) => {
                let value = e.target.value;

			setEmail(value)
            }
            const handleInputChangegender = (e) => {
                let value = e.target.value;

            setGender(value)
            }

    // const handleSetState = (e, setFunctionName) => {
    //     let val = e.target.value;
    //     setFunctionName(val);
    //     // setPassword(val);
    //   };

    const handleSubmit = async (e) => {
        e.preventDefault()
        const data = {
            firstName: firstName,
            lastName: lastName,
            gender: gender,
            passions: passions,
            email: email
        }
        const {day, month, year} = data
        data.dob = `${day}-${month}-${year}`;
        delete data.day
        delete data.month
        delete data.year

        await axiosRequest("put", apiRoutes.updateProfile, data)
            .then((res) => {
                console.log(res)

                if(res?.data){
                    showToast(res?.data?.messsage || "Your request was successful", "success")
                }
                history.push("/uploadphotos");

            })
            .catch(err => {
                let {data} = err.response;
                if(data){
                    return showToast(data.error, "error")
                } else{
                    showToast("!Oops something went wrong. We are looking at it", "error")
                }
            })
    }

    return (
        <>
<Modal onClose={closeModal} visible={modal}>
                    <TermsModal close={closeModal}/>
                </Modal>
                <Modal onClose={closeModal} visible={modal} removeCloseIcon={true}>
                    <TermsModal close={closeModal}/>
                </Modal>
            {/* {termsAndCondition && termsAndCondition ? */}
                <Modal onClose={closeModal} visible={modal} removeCloseIcon={true}>
                    <TermsModal close={closeModal}/>
                </Modal>
            {/* : ""
            } */}
        </>
    )
}
