import { useEffect, useState } from "react"
import { useSelector } from "react-redux";
import homeIcon from "../../icons/home.svg"
import settingsIcon from "../../icons/settings.svg"
import worldIcon from "../../icons/world.svg"


export const LeftDashboard = () => {

    const [profilePic, setProfilePic] = useState();

    const {user} = useSelector((state) => state.auth);

    useEffect(()=>{
        let pictures = user?.pictures;
        if(pictures.length > 0){
            const reArrange = pictures.sort(function(a, b) {
                return a.id - b.id;
            });

            setProfilePic(reArrange[0]?.url)
        }
    }, [])

    return (
        <>
            <div className="left" style={{ width: "30%"}}>
                <div className="flex w-full" style={{backgroundColor: "#7B61FF", height: "7rem"}}>
                    <div className="flex w-1/2 justify-around items-center">
                        <div className="">
                            <img style={{ borderRadius: "50%", height: "50px", width: "50px"}} src={profilePic} alt="home" /> 
                        </div>
                        <div className="mr-16" >
                            <div className="text-white">{user.firstName}</div>
                        </div>
                    </div>

                    <div className="flex w-1/2 justify-around items-center">

                        <div className="my-8" style={{border: "2px red black",}}>
                            <img className="my-4" style={{ height: "25px", width: "25px"}} src={homeIcon} alt="home" /> 
                        </div>
                        <div className="my-8" style={{border: "2px red black"}}>
                            <img className="my-4" style={{ height: "25px", width: "25px"}} src={settingsIcon} alt="home" /> 
                        </div>
                        <div className="my-8" style={{border: "2px red black",}}>
                                <img className="my-4" style={{ height: "25px", width: "25px"}} src={worldIcon} alt="home" /> 
                        </div>
                    </div>

                </div>

            </div>

        </>
    )
}
