import { useEffect, useState } from "react"
import { ModalCancel } from "../../images/cancel"

export const Modal = ({ children, visible, onClose,removeCloseIcon, ...rest}, cancelBtn) => {

    const [_visible, setVisible] = useState(false)

    useEffect(() => {
        setVisible(visible)
    }, [visible])
    return (
        <div className={`modal flex h-screen w-full items-center justify-center
        ${!_visible && "hidden"}
        ${rest?.className}`} onClick={() => {
            if(onClose) {
                onClose()
            }
        }}>

            <div className="container" onClick={(e) => e.stopPropagation()}>
				<div className="flex items-center justify-end">
					<span
						className="close-span pointer p-5 hover:bg-primary"
						onClick={onClose}
					>
						{" "}
                        {removeCloseIcon ? "" :
						    <ModalCancel />
                        }
                        {" "}
					</span>
				</div>
				{visible && <div className="modal-content">{children}</div>}
			</div>
        </div>
    )
}
