import { useState } from "react"
import { Button } from "../../components/button/Button"
import { Footer } from "../../components/layout/Footer"
import { Modal } from "../../components/modal/Modal"
import LoginModal from "./partials/LoginModal"

const Homepage = () => {
    const [modal, setModal] = useState(false)
    const [type, setType] = useState("login")

    const closeModal = () => {
        setModal(false)
    }
    return (
        <div className="home">
            <div className="main-cont">
                <div className="top-bar flex justify-between">
                    <div className="logo">
                        <h3>flenjo</h3>
                    </div>

                    <div className="login-btn">
                        <Button text="Sign in" type="submit" className="log-btn" onClick={() => {
                            setModal(true); 
                            setType("login")
                            }} 
                        />
                    </div>
                </div>

                <div className="body-sect">
                    <div className="top">
                        <h2>Swipe around <br /> meet people</h2>

                    </div>

                    <div className="regist">
                        <Button text="create account" className="reg-btn" onClick={() => {
                            setModal(true); 
                            setType("signup")
                            }} 
                        />
                    </div>
                </div>
            </div>

            <Modal onClose={closeModal} visible={modal}>
                <LoginModal close={closeModal} type={type} />
            </Modal>

            <Footer />
        </div>
    )
}

export default Homepage
