import { useState } from "react"
import { Button, Input } from "../../components";
import { Checkbox } from "../../components/input/Checkbox";
import { Modal } from "../../components/modal/Modal";
import { femaleIcon, maleIcon } from "../../icons";
import babe from "../../images/babe.png";
import TermsModal from "./partials/Terms";
import axiosRequest from "../../utils/apiservices";
import { apiRoutes } from "../../utils/apiRoutes";

export const AccountSetup = () => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [modal, setModal] = useState(true);
  const [firstName, setFirstName] = useState();
  const [lastName, setLastName] = useState();
  const [dob, setDob] = useState();
  const [gender, setGender] = useState();
//   const [firstName, setFirstName] = useState();

    const handleSubmit = async (e) => {
        e.preventDefault();

        setIsSubmitting(true);

        try {
          await axiosRequest("put", apiRoutes.verificationCode, {
            type: "phone",
            phone: "0" + phoneNumber.toString(),
          });

          verifyPhone();
        } catch (err) {
          //err handler here and notification if needed
        } finally {
          setIsSubmitting(false);
        }
      };


    const closeModal = () => {
        setModal(false)
    }
    return (
        <>

            <div className="account-cont flex justify-between">
                <div className="left w-1/2">
                    <div className="-page-title">
                        <h3>Set up your Flenjo account</h3>
                    </div>

                    <div className="details-fill ">
                        <div className="name-cont flex">
                            <div className="w-1/2 ">
                                <Input
                                    label="Surname (Family name)"
                                    name="surname"
                                    onChange={""}
                                    placeholder="Suraname"
                                    className="ml-0"
                                />
                            </div>
                            <div className="w-1/2 ">
                                <Input
                                    label="First Name"
                                    name="firstname"
                                    onChange={""}
                                    placeholder="First name"
                                />
                            </div>
                        </div>

                        <div className="sec-cont flex">
                            <div className="w-1/2 ">
                                <Input
                                    label="Email"
                                    name="email"
                                    onChange={""}
                                    placeholder="Precaptured email"
                                />
                            </div>
                            <div className="w-1/2 flex justify-between">
                                <Input
                                    label="Birthday"
                                    name="birthday"
                                    onChange={""}
                                    placeholder="MM"
                                    className="text-center"
                                />
                                <Input
                                    label=""
                                    name="birthday"
                                    onChange={""}
                                    placeholder="DD"
                                    className="mt-8 text-center"
                                />
                                <Input
                                    name="birthday"
                                    onChange={""}
                                    placeholder="YY"                                   
                                    className="mt-8 text-center"
                                />
                            </div>
                        </div>

                        <div className="gender-sect">
                            <p className="gender-text">Gender</p>
                            <div className="gender-select flex">
                                <div className="gender male flex"><img src={maleIcon} alt="MALE" /> <span>Male</span></div>
                                <div className="gender female flex"><img src={femaleIcon} alt="FEMALE" /><span>Female</span></div>
                            </div>
                            <div className="show-gender"><Checkbox label="show my gender on my profile" /></div>
                        </div>

                        <div className="passion ">
                            <p className="optional">Choose your passion (Optional)</p>
                            <br />
                            <span>Let everyone know what you are passionate about</span>

                            <div className="select-passion flex">
                                <div className="item">Foodie</div>
                                <div className="item">Driving</div>
                                <div className="item">Drumming</div>
                                <div className="item">Reading</div>
                                <div className="item">Singing</div>
                            </div>

                            <div className="more"><span>+</span> Show more</div>
                        </div>

                        <div className="continue-btn">
                            <Button text="Continue" className="settings-btn"/>
                            type="submit"
                            disabled={isSubmitting}
                        </div>
                    </div>
                </div>
                <div className="right w-1/2">
                    <img src={babe} alt="Girl" />
                </div>
            </div>

            <Modal onClose={closeModal} visible={modal} removeCloseIcon={true}>
                <TermsModal close={closeModal} />
            </Modal>
        </>
    )
}
