import React, { useState } from "react";
import { Button, Input } from "../../../components";
import axiosRequest from "../../../utils/apiservices";
import { apiRoutes } from "../../../utils/apiRoutes";

const PhoneLogin = ({ verifyPhone, goBack, setPhoneNumber, phoneNumber }) => {
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    setIsSubmitting(true);

    try {
      await axiosRequest("post", apiRoutes.verificationCode, {
        type: "phone",
        phone: "0" + phoneNumber.toString(),
      });

      verifyPhone();
    } catch (err) {
      //err handler here and notification if needed
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleChange = (e) => {
    let val = e.target.value;
    if (val.length === 11) return;
    setPhoneNumber(val);
  };

  return (
    <form className="phone" onSubmit={handleSubmit}>
      <div className="form-title">
        <h4>Enter Phone number</h4>
        <p>
          We will send a text with verification code. Message and data rate may
          apply.{" "}
        </p>
      </div>

      <div className="phone-div mt-3 flex justify-between">
        <button disabled>+234</button>

        <Input
          placeholder="80xxxxxxxx"
          name="phoneNumber"
          type="number"
          className="number-input"
          value={phoneNumber}
          onChange={handleChange}
          required
        />
