import { local, session } from "./storage";
import cogoToast from "cogo-toast";

export const _roleFinance = 13;

export const generateActions = (action) => {
	action = action.toUpperCase();
	return {
		REQUEST: `${action}_REQUEST`,
		SUCCESS: `${action}_SUCCESS`,
		FAILURE: `${action}_FAILURE`,
	};
};

export const _isAnEmpytyObject = (obj) => {
	for (var key in obj) {
		if (obj.hasOwnProperty(key)) return false;
	}
	return true;
};

export const _textCapitalize = (text) => {
	return text && text.charAt(0).toUpperCase() + text.slice(1).toLowerCase();
};

export const getToken = () => {
	let token = "";
	const local_token = local.getObject("token").access;
	token =
		(_isAnEmpytyObject(local_token) ? "" : local_token) || session.get("token");
	return token;
};

export const getUser = () => {
	const local_user = session.getObject("user");
	const user = _isAnEmpytyObject(local_user) ? null : local_user;
	return user;
};

export const _removeUnderscore = (word) => {
	return word?.split("_").join(" ");
};

export const isUserLoggedIn = () => {
	const user = getUser();
	if (!_isAnEmpytyObject(user)) return true;
	return false;
};

export const showToast = (message, type) => {
	if (type) type = type.toLowerCase();

	switch (type) {
		case "success":
			cogoToast.success(message, { position: "top-right" });
			break;
		case "info":
			cogoToast.info(message, { position: "top-right" });
			break;
		case "loading":
			cogoToast.loading(message, { position: "top-right" });
			break;
		case "warn":
			cogoToast.warn(message, { position: "top-right" });
			break;
		case "error":
			cogoToast.error(message, { position: "top-right" });
			break;

		default:
			cogoToast.info(message, { position: "top-right" });
			break;
	}
};

export * from "./storage";
