import { useEffect, useState } from "react"
import { useSelector } from "react-redux";
import homeIcon from "../../icons/home.svg"
import settingsIcon from "../../icons/settings.svg"


export const LeftDashboard = () => {

    const [profilePic, setProfilePic] = useState();

    const {user} = useSelector((state) => state.auth);

    useEffect(()=>{
        let pictures = user?.pictures;
        if(pictures.length > 0){
            const reArrange = pictures.sort(function(a, b) {
                return a.id - b.id;
            });

            setProfilePic(reArrange[0]?.url)
        }
    }, [])

    return (
        <>
            <div className="left" style={{ width: "30%"}}>
                <div className="grid grid-cols-5 gap-1" style={{backgroundColor: "#7B61FF"}}>

                    <div className="my-8" style={{border: "2px red black", textAlign:"center", justifyContent:"center", height: "55px"}}>
                        <img style={{ borderRadius: "50%", height: "55px", width: "55px"}} src={profilePic} alt="home" /> 
                    </div>
                    <div className="my-8" style={{border: "2px red black", textAlign:"center", justifyContent:"center", height: "55px"}}>
                        <div className="my-4" style={{borderRadius: "50%", height: "55px", width: "55px", color: "white"}}>{user.firstName}</div>
                    </div>

                    <div className="my-8" style={{border: "2px red black", textAlign:"center", justifyContent:"center", height: "55px"}}>
                        <img className="my-4" style={{ height: "25px", width: "25px"}} src={homeIcon} alt="home" /> 
                    </div>
                    <div className="my-8" style={{border: "2px red black", textAlign:"center", justifyContent:"center", height: "55px"}}>
                        <img className="my-4" style={{ height: "25px", width: "25px"}} src={settingsIcon} alt="home" /> 
                    </div>
                    <div className="my-8" style={{border: "2px red black", textAlign:"center", justifyContent:"center", height: "55px"}}>
                            <img className="my-4" style={{ height: "25px", width: "25px"}} src={settingsIcon} alt="home" /> 
                    </div>

                </div>

            </div>

        </>
    )
}
