import { SIGNUP, LOGIN } from "../actions/types";
import { decryptData } from "../../helpers";

const initialState = {
  user: decryptData(localStorage.getItem("user")) || null,
};

const auth = (state = initialState, action) => {
  const { type, payload } = action;

  switch (type) {
    case SIGNUP.SUCCESS:
      return { ...state, user: payload };

    default:
      return state;
  }
};

export default auth;
